package by.it_academy.less02;

public class Execise01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x =7;
		int y =3;
	//	float z=0;
		System.out.println("Cумма чисел "+ x + " и " + y + " равна " + (x+y));
		System.out.println("Разность чисел "+ x + " и " + y + " равна " + (x-y));
		System.out.println("Произведение чисел "+ x + " и " + y + " равно " + (x*y));
		System.out.println("Частное чисел "+ x + " и " + y + " равно " + (x/y));
	}

}
